import React, { useState } from "react";
import "./UI/RevUi.css";
import "./UI/UI.css";
import "./UI/UploadButton.css";
import "./UI/animation.css";

const RevAIComponent = () => {
  const [transcription, setTranscription] = useState("");
  const [audioFile, setAudioFile] = useState();
  const [fileName, setFileName] = useState("");

  const transcribeAudio = async () => {
    try {
      if (!audioFile) {
        alert("Please select an audio file.");
        return;
      }

      const formData = new FormData();
      formData.append("audioUrl", audioFile);

      const postOptions = {
        method: "POST",
        body: formData,
      };

      const response = await fetch(
        "http://localhost:5000/api/transcribe",
        postOptions
      );
      const { jobId } = await response.json();

      const checkStatus = async () => {
        try {
          const transcriptResponse = await fetch(
            `http://localhost:5000/api/transcribe/${jobId}`
          );
          const { status, transcript } = await transcriptResponse.json();

          if (status === "transcribed") {
            setTranscription(transcript);
          } else {
            setTimeout(checkStatus, 3000);
          }
        } catch (error) {
          console.error("Error fetching transcript:", error);
        }
      };

      checkStatus();
    } catch (error) {
      console.error("Error transcribing audio:", error);
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setAudioFile(file);
    setFileName(file ? file.name : "");
  };

  return (
    <div className="container">
      <div className="center">
        <div className="button">
          <label
            className="file-label"
            htmlFor="file-upload"
            data-tooltip="Size: 20Mb"
          >
            <span className="button-wrapper">
              <span className="text">Upload</span>
              <span className="icon">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  aria-hidden="true"
                  role="img"
                  width="2em"
                  height="2em"
                  preserveAspectRatio="xMidYMid meet"
                  viewBox="0 0 24 24"
                >
                  <path
                    fill="none"
                    stroke="currentColor"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M12 15V3m0 12l-4-4m4 4l4-4M2 17l.621 2.485A2 2 0 0 0 4.561 21h14.878a2 2 0 0 0 1.94-1.515L22 17"
                  ></path>
                </svg>
              </span>
            </span>
            <input
              id="file-upload"
              className="inputB"
              type="file"
              onChange={handleFileChange}
              accept="audio/*"
            />
          </label>
        </div>
        <p className="file-name">{fileName && `Selected File: ${fileName}`}</p>
        <button onClick={transcribeAudio}>Transcribe Audio</button>
        <div>
          <button className="btn">
            <i className="animation"></i>BUTTON<i className="animation"></i>
          </button>
        </div>
      </div>
      <div>
        <p>Transcription Result:</p>
        <p>{transcription}</p>
      </div>
    </div>
  );
};

export default RevAIComponent;
