import React, { useState } from 'react';
import axios from 'axios';
import './RevUi.css';

const RevAIComponent = () => {
  const [transcription, setTranscription] = useState('');
  const [audioFile, setAudioFile] = useState(null);

  const transcribeAudio = async () => {
    try {
      if (!audioFile) {
        alert('Please select an audio file.');
        return;
      }
  
      const formData = new FormData();
      formData.append('audioUrl', audioFile); // Send the audio file URL to backend
  
      const response = await axios.post(
        'http://localhost:5000/api/transcribe', // Send request to your backend
        formData,
        {
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );
  
      const jobId = response.data.jobId;
  
      // Polling method (recommended is to use webhooks for production)
      const checkStatus = async () => {
        const transcriptResponse = await axios.get(
          `http://localhost:5000/api/transcript/${jobId}`, // Fetch transcript from your backend using the jobId
          {
            headers: {
              'Content-Type': 'application/json'
            }
          }
        );
        
        // Check if transcription is completed, and fetch the transcript
        if (transcriptResponse.data.status === "transcribed") {
          const text = transcriptResponse.data.transcript;
          setTranscription(text);
        } else {
          // If transcription is not completed, wait and check again after a certain interval
          setTimeout(checkStatus, 3000); // Check status again after 3 seconds
        }
      };
  
      checkStatus(); // Start checking for transcript completion
    } catch (error) {
      console.error('Error transcribing audio:', error);
    }
  };  

  const handleFileChange = (event) => {
    setAudioFile(event.target.files[0]);
  };

  return (
    <div className="container">
      <input className="file-input" type="file" onChange={handleFileChange} accept="audio/*" />
      <button onClick={transcribeAudio}>Transcribe Audio</button>
      <div className="transcription-result">
        <p>Transcription Result:</p>
        <p>{transcription}</p>
      </div>
    </div>
  );
};

export default RevAIComponent;
