import React, { useState } from 'react';
import './RevUi.css';
import './UI.css';

const RevAIComponent = () => {
  const [transcription, setTranscription] = useState('');
  const [audioFile, setAudioFile] = useState();

  const transcribeAudio = async () => {
    try {
      if (!audioFile) {
        alert('Please select an audio file.');
        return;
      }

      const formData = new FormData();
      formData.append('audioUrl', audioFile);

      const postOptions = {
        method: 'POST',
        body: formData,
      };

      const response = await fetch('http://localhost:5000/api/transcribe', postOptions);
      const { jobId } = await response.json();

      const checkStatus = async () => {
        try {
          const transcriptResponse = await fetch(`http://localhost:5000/api/transcribe/${jobId}`);
          const { status, transcript } = await transcriptResponse.json();

          if (status === 'transcribed') {
            setTranscription(transcript);
          } else {
            setTimeout(checkStatus, 3000);
          }
        } catch (error) {
          console.error('Error fetching transcript:', error);
        }
      };

      checkStatus();
    } catch (error) {
      console.error('Error transcribing audio:', error);
    }
  };

  const handleFileChange = (event) => {
    setAudioFile(event.target.files[0]);
  };

  return (
    <div>
      <div>
        <div>
          <input className='inputB' type="file" onChange={handleFileChange} accept="audio/*" />
        </div>
        <div>
        <div class="button" data-tooltip="Size: 20Mb">
<div class="button-wrapper">
  <div class="text">Download</div>
    <span class="icon">
      <svg xmlns="http://www.w3.org/2000/svg" aria-hidden="true" role="img" width="2em" height="2em" preserveAspectRatio="xMidYMid meet" viewBox="0 0 24 24"><path fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 15V3m0 12l-4-4m4 4l4-4M2 17l.621 2.485A2 2 0 0 0 4.561 21h14.878a2 2 0 0 0 1.94-1.515L22 17"></path></svg>
    </span>
  </div>
</div>
        </div>
        <button onClick={transcribeAudio}>Transcribe Audio</button>
      </div>
      <div>
        <p>Transcription Result:</p>
        <p>{transcription}</p>
      </div>
    </div>
  );
};

export default RevAIComponent;
