import React, { useState } from 'react';
import axios from 'axios';
import './RevUi.css';

const RevAIComponent = () => {
  const [transcription, setTranscription] = useState('');
  const [audioFile, setAudioFile] = useState(null);

  const transcribeAudio = async () => {
    try {
      if (!audioFile) {
        alert('Please select an audio file.');
        return;
      }

      const formData = new FormData();
      formData.append('audioFile', audioFile); // Update key to match backend

      const response = await axios.post(
        'http://localhost:5000/api/transcribe',
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data', // Set proper content type for FormData
          },
        }
      );

      const jobId = response.data.jobId;

      // Rest of your code for checking transcription status...

    } catch (error) {
      console.error('Error transcribing audio:', error);
    }
  };

  const handleFileChange = (event) => {
    setAudioFile(event.target.files[0]);
  };

  return (
    <div>
      <div>
        <div>
          <input type="file" onChange={handleFileChange} accept="audio/*" />
        </div>
        <button onClick={transcribeAudio}>Transcribe Audio</button>
      </div>
      <div>
        <p>Transcription Result:</p>
        <p>{transcription}</p>
      </div>
    </div>
  );
};

export default RevAIComponent;
